def translate_from_first_star(star_list):
    new_star_list = []
    first_x = star_list[0][0]
    first_y = star_list[0][1]

    new_star_list = [(star_pos_tuple[0] - first_x, star_pos_tuple[1] - first_y) for star_pos_tuple in star_list]

    return new_star_list


