number_of_stars_on_board = 0
for ypos in range(-int(BOARD_HEIGHT/2), int(BOARD_HEIGHT/2)):
    for xpos in range(-int(BOARD_WIDTH/2), int(BOARD_WIDTH/2)):

        current_pos = (xpos, ypos)
        #print_debug("")
